  imports: [CommonModule, FormsModule, ReactiveFormsModule, RouterLink],
  templateUrl: './employees.component.html',
  styleUrls: ['./employees.component.css']
})
export class EmployeesComponent implements OnInit {
  employees: Employee[] = [];
  jobDescriptions: JobDescription[] = [];
  employeeForm: FormGroup;
  editingEmployee: Employee | null = null;
  errorMessage: string | null = null;
loading: any;
showAddForm: any;

  constructor(
    private employeeService: EmployeeService,
    private jobDescriptionService: JobDescriptionService,
    private formBuilder: FormBuilder
  ) {
    this.employeeForm = this.formBuilder.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      position: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      hire_date: ['', Validators.required],
      phone: [''],
      gender: [''],
      location: [''],
      notes: [''],
      jobdescription: [''] // tu gères toi-même le poste
    });
  }

  ngOnInit(): void {
    this.loadEmployees();
    this.loadJobDescriptions();
  }

  loadEmployees(): void {
    this.employeeService.getEmployees().subscribe({
      next: (employees) => this.employees = employees,
      error: (err) => {
        console.error('Erreur chargement employés:', err);
        this.errorMessage = 'Erreur lors du chargement des employés.';
      }
    });
  }

  loadJobDescriptions(): void {
    this.jobDescriptionService.getJobDescriptions().subscribe({
      next: (jobs) => this.jobDescriptions = jobs,
      error: (err) => console.error('Erreur chargement fiches de poste:', err)
    });
  }

  onSubmit(): void {
    if (!this.employeeForm.valid) {
      this.errorMessage = 'Veuillez remplir tous les champs obligatoires.';
      return;
    }

    const formValue = this.employeeForm.value;
    const employeeData = {
      name: formValue.name,
      position: formValue.position,
      email: formValue.email,
      hire_date: formValue.hire_date,
      phone: formValue.phone || '',
      gender: formValue.gender || '',
      location: formValue.location || '',
      notes: formValue.notes || '',
      jobDescriptions: formValue.jobDescription || ''
    } as Employee;

    if (this.editingEmployee) {
      this.employeeService.updateEmployee(this.editingEmployee.id!, employeeData).subscribe({
        next: (updated) => {
          const index = this.employees.findIndex(e => e.id === updated.id);
          if (index !== -1) this.employees[index] = updated;
          this.cancelEdit();
          this.errorMessage = null;
        },
        error: (err) => {
          console.error('Erreur mise à jour:', err);
          this.errorMessage = err.error?.message || 'Erreur lors de la mise à jour';
        }
      });
    } else {
      this.employeeService.createEmployee(employeeData).subscribe({
        next: (newEmp) => {
          this.employees.push(newEmp);
          this.cancelEdit();
          this.errorMessage = null;
        },
        error: (err) => {
          console.error('Erreur création:', err);
          this.errorMessage = err.error?.message || 'Erreur lors de la création';
        }
      });
    }
  }

  editEmployee(emp: Employee): void {
    this.editingEmployee = emp;
    this.employeeForm.patchValue({
      name: emp.name,
      position: emp.position,
      email: emp.email,
      hire_date: emp.hire_date,
      phone: emp.phone || '',
      gender: emp.gender || '',
      location: emp.location || '',
      notes: emp.notes || '',
      jobDescriptions: emp.jobDescription || ''
    });
  }

  deleteEmployee(emp: Employee): void {
    if (!window.confirm(`Supprimer ${emp.name} ?`)) return;
    this.employeeService.deleteEmployee(emp.id!).subscribe({
      next: () => this.employees = this.employees.filter(e => e.id !== emp.id),
      error: (err) => {
        console.error('Erreur suppression:', err);
        this.errorMessage = 'Erreur lors de la suppression de l\'employé.';
      }
    });
  }

  cancelEdit(): void {
    this.editingEmployee = null;
    this.employeeForm.reset();
    this.errorMessage = null;
  }

  private parseIntegerField(value: any): number | null {
    if (!value || value === '' || value === 'null' || value === 'undefined') return null;
    const parsed = parseInt(value, 10);
    return isNaN(parsed) ? null : parsed;
  }
}
<div class="p-6">
