import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule } from '@angular/forms';
import { RouterLink } from '@angular/router';
import { EmployeeService } from '../../services/employee.service';
import { Employee } from '../../models/employee.model';

@Component({
  selector: 'app-employees',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterLink],
  templateUrl: './employees.component.html',
  styleUrls: ['./employees.component.css']
})
export class EmployeesComponent implements OnInit {
  employees: Employee[] = [];
  employeeForm: FormGroup;
  showAddForm: boolean = false;
  editingEmployee: Employee | null = null;
  loading: boolean = false;
  errorMessage: string | null = null;

  constructor(
    private employeeService: EmployeeService,
    private formBuilder: FormBuilder
  ) {
    this.employeeForm = this.formBuilder.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      position: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      hire_date: ['', Validators.required],
      phone: [''],
      gender: [''],
      location: [''],
      notes: ['']
    });
  }

  ngOnInit(): void {
    this.loadEmployees();
  }

  loadEmployees(): void {
    this.loading = true;
    this.errorMessage = null;
    this.employeeService.getEmployees().subscribe({
      next: (employees) => {
        this.employees = employees;
        this.loading = false;
      },
      error: (err) => {
        console.error('Error loading employees:', err);
        this.errorMessage = 'Erreur lors du chargement des employés. Veuillez réessayer plus tard.';
        this.loading = false;
      }
    });
  }

  onSubmit(): void {
    if (this.employeeForm.valid) {
      const employeeData = this.employeeForm.value;

      if (this.editingEmployee) {
        this.employeeService.updateEmployee(this.editingEmployee.id!, employeeData).subscribe({
          next: (updatedEmployee) => {
            const index = this.employees.findIndex(emp => emp.id === updatedEmployee.id);
            if (index !== -1) {
              this.employees[index] = updatedEmployee;
            }
            this.cancelEdit();
            this.errorMessage = null;
          },
          error: (err) => {
            console.error('Error updating employee:', err);
            this.errorMessage = 'Erreur lors de la mise à jour de l\'employé.';
          }
        });
      } else {
        this.employeeService.createEmployee(employeeData).subscribe({
          next: (newEmployee) => {
            this.employees.push(newEmployee);
            this.cancelEdit();
            this.errorMessage = null;
          },
          error: (err) => {
            console.error('Error creating employee:', err);
            this.errorMessage = 'Erreur lors de la création de l\'employé.';
          }
        });
      }
    }
  }

  editEmployee(employee: Employee): void {
    this.editingEmployee = employee;
    this.showAddForm = false;
    this.employeeForm.patchValue({
      name: employee.name,
      position: employee.position,
      email: employee.email,
      hire_date: employee.hire_date,
      phone: employee.phone || '',
      gender: employee.gender || '',
      location: employee.location || '',
      notes: employee.notes || ''
    });
  }

  deleteEmployee(employee: Employee): void {
    if (window.confirm(`Êtes-vous sûr de vouloir supprimer ${employee.name} ?`)) {
      this.employeeService.deleteEmployee(employee.id!).subscribe({
        next: () => {
          this.employees = this.employees.filter(emp => emp.id !== employee.id);
          this.errorMessage = null;
        },
        error: (err) => {
          console.error('Error deleting employee:', err);
          this.errorMessage = 'Erreur lors de la suppression de l\'employé.';
        }
      });
    }
  }

  cancelEdit(): void {
    this.editingEmployee = null;
    this.showAddForm = false;
    this.employeeForm.reset();
  }
}
