import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { EmployeeSkill, EmployeeSkillService } from '../../services/employee-skill.service';

@Component({
  selector: 'app-employee-skills',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './employee-skills.component.html',
  styleUrls: ['./employee-skills.component.css']
})
export class EmployeeSkillsComponent implements OnInit {
  skills: EmployeeSkill[] = [];
  loading = false;
  error = '';

  constructor(private skillService: EmployeeSkillService) {}

  ngOnInit(): void {
    this.loadSkills();
  }

  loadSkills(): void {
    this.loading = true;
    this.error = '';
    this.skillService.getAll().subscribe({
      next: data => {
        this.skills = data;
        this.loading = false;
      },
      error: err => {
        this.error = err?.message || 'Erreur lors du chargement';
        this.loading = false;
      }
    });
  }

  deleteSkill(employee_id: number, skill_id: number): void {
    if (!confirm('Voulez-vous vraiment supprimer cette compétence ?')) return;

    this.skillService.delete(employee_id, skill_id).subscribe({
      next: () => this.loadSkills(),
      error: err => this.error = err?.message || 'Erreur lors de la suppression'
    });
  }

  // Méthode d'ajout si service crée
  addSkill(skillData: any): void {
    if (!skillData) return;

    // ⚠️ Vérifie que ton service a une méthode create
    if (!this.skillService.create) {
      console.error("Méthode create non définie dans EmployeeSkillService");
      return;
    }

    this.skillService.create(skillData).subscribe({
      next: () => this.loadSkills(),
      error: err => this.error = err?.message || "Erreur lors de l'ajout"
    });
  }
}
<div class="p-6">
  <h2 class="text-3xl font-bold text-primary mb-6">Liste des Compétences des Employés</h2>

  <!-- Messages -->
  <div *ngIf="loading" class="text-center text-primary mt-4">
    Chargement des compétences...
  </div>
  <div *ngIf="error" class="mt-4 p-3 bg-red-100 text-red-700 rounded-md">
    {{ error }}
  </div>

  <!-- Formulaire d'ajout d'une compétence -->
  <div class="card mb-6 p-4 border rounded-md shadow-sm">
    <h3 class="text-xl font-semibold text-dark mb-4">Ajouter une compétence</h3>
    <form #skillForm="ngForm" (ngSubmit)="addSkill(skillForm)">
      <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Employé *</label>
          <input type="number" name="employee_id" ngModel required class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Compétence *</label>
          <input type="number" name="skill_id" ngModel required class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Niveau *</label>
          <input type="number" name="actual_skill_level_id" ngModel required class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Date acquise</label>
          <input type="date" name="acquired_date" ngModel class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Certification</label>
          <input type="text" name="certification" ngModel class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Dernière évaluation</label>
          <input type="date" name="last_evaluated_date" ngModel class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
      </div>
      <div class="mt-4">
        <button type="submit" class="btn btn-primary">Ajouter</button>
      </div>
    </form>
  </div>

  <!-- Tableau des compétences -->
  <div *ngIf="!loading && skills.length" class="card mt-6">
    <div class="overflow-x-auto">
      <table class="min-w-full table-auto border border-gray-200">
        <thead class="bg-gray-50">
          <tr>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Employé</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Compétence</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Niveau</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date acquise</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Certification</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dernière évaluation</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
          </tr>
        </thead>
        <tbody class="bg-white divide-y divide-gray-200">
          <tr *ngFor="let s of skills" class="hover:bg-gray-50">
            <td class="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
              {{ s.Employee?.name || s.employee_id }}
            </td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
              {{ s.Skill?.name || s.skill_id }}
            </td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
              {{ s.SkillLevel?.name || s.actual_skill_level_id }}
            </td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
              {{ s.acquired_date ? (s.acquired_date | date:'dd/MM/yyyy') : '-' }}
            </td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
              {{ s.certification || '-' }}
            </td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
              {{ s.last_evaluated_date ? (s.last_evaluated_date | date:'dd/MM/yyyy') : '-' }}
            </td>
            <td class="px-6 py-4 whitespace-nowrap text-sm font-medium">
              <button class="text-red-600 hover:text-red-900" 
                      (click)="deleteSkill(s.employee_id, s.skill_id)">
                Supprimer
              </button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>

  <div *ngIf="!loading && skills.length === 0" class="text-center text-gray-600 mt-4">
    Aucune compétence trouvée.
  </div>
</div>
