import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormsModule, FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { catchError, finalize, of } from 'rxjs';
import { EmployeeService } from '../../services/employee.service';
import { SkillService } from '../../services/skill.service';
import { Employee, Skill, SkillType, SkillLevel, EmployeeSkill } from '../../models/employee.model';

@Component({
  selector: 'app-profile',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, FormsModule],
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  // Injection des services réels
  private fb = inject(FormBuilder);
  private route = inject(ActivatedRoute);
  private router = inject(Router);
  private employeeService = inject(EmployeeService);
  private skillService = inject(SkillService);

  // Propriétés
  profileForm!: FormGroup;
  employee: Employee | null = null;
  skills: Skill[] = [];
  skillTypes: SkillType[] = [];
  skillLevels: SkillLevel[] = [];
  employeeSkills: EmployeeSkill[] = [];

  loading = false;
  saving = false;
  isEditing = false;
  errorMessage = '';
  successMessage = '';

  employeeId: string = '';

  ngOnInit(): void {
    this.employeeId = this.route.snapshot.paramMap.get('id') || '';
    if (!this.employeeId) {
      this.errorMessage = 'ID employé manquant';
      return;
    }

    this.initializeForm();
    this.loadInitialData();
  }

  private initializeForm(): void {
    this.profileForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(2)]],
      position: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      hire_date: ['', Validators.required],
      phone: [''],
      gender: [''],
      location: [''],
      notes: [''],
      skills: this.fb.array([])
    });

    this.profileForm.disable();
  }

  private loadInitialData(): void {
    this.loading = true;
    this.clearMessages();

    Promise.all([this.loadEmployee(), this.loadSkillsData()])
      .finally(() => this.loading = false);
  }

  private loadEmployee(): Promise<void> {
    return new Promise((resolve, reject) => {
      this.employeeService.getEmployeeById(this.employeeId)
        .pipe(
          catchError(err => {
            this.errorMessage = 'Erreur lors du chargement des données employé';
            console.error(err);
            reject(err);
            return of(null);
          })
        )
        .subscribe((employee: Employee | null) => {
          if (employee) {
            this.employee = employee;
            this.populateEmployeeForm(employee);
            this.loadEmployeeSkills();
          }
          resolve();
        });
    });
  }

  private loadSkillsData(): Promise<void> {
    return new Promise((resolve, reject) => {
      Promise.all([
        this.skillService.getSkills().toPromise(),
        this.skillService.getSkillTypes().toPromise(),
        this.skillService.getSkillLevels().toPromise()
      ])
      .then(([skills, skillTypes, skillLevels]) => {
        this.skills = skills;
        this.skillTypes = skillTypes;
        this.skillLevels = skillLevels;
        resolve();
      })
      .catch(err => {
        this.errorMessage = 'Erreur lors du chargement des compétences';
        console.error(err);
        reject(err);
      });
    });
  }

  private loadEmployeeSkills(): void {
    // Suppose que employeeSkills est déjà rempli côté backend ou via un service
    this.employeeSkills = (this.employee?.skills || []) as EmployeeSkill[];
    this.populateSkillsForm();
  }

  private populateEmployeeForm(employee: Employee): void {
    this.profileForm.patchValue({
      name: employee.name,
      position: employee.position,
      email: employee.email,
      hire_date: employee.hire_date,
      phone: employee.phone,
      gender: employee.gender,
      location: employee.location,
      notes: employee.notes
    });
  }

  private populateSkillsForm(): void {
    const skillsArray = this.getSkillsFormArray();
    skillsArray.clear();
    this.employeeSkills.forEach(skill => skillsArray.push(this.createSkillFormGroup(skill)));
  }

  private createSkillFormGroup(skill?: EmployeeSkill): FormGroup {
    return this.fb.group({
      id: [skill?.id || ''],
      skill_id: [skill?.skill_id || '', Validators.required],
      actual_skill_level_id: [skill?.skill_level_id || '', Validators.required],
      acquired_date: [skill?.acquired_date || ''],
      certification: [skill?.certification || ''],
      last_evaluated_date: [skill?.last_evaluated_date || '']
    });
  }

  get skillsFormArray(): FormArray {
    return this.profileForm.get('skills') as FormArray;
  }

  addSkill(): void {
    this.skillsFormArray.push(this.createSkillFormGroup());
  }

  removeSkill(index: number): void {
    const skillControl = this.skillsFormArray.at(index);
    const skillId = skillControl.get('id')?.value;
    if (skillId) this.deleteEmployeeSkill(skillId);
    this.skillsFormArray.removeAt(index);
  }

  private deleteEmployeeSkill(skillId: string): void {
    this.skillService.deleteEmployeeSkill(this.employeeId, skillId)
      .pipe(catchError(err => {
        this.errorMessage = 'Erreur lors de la suppression de la compétence';
        console.error(err);
        return of(null);
      }))
      .subscribe(() => {
        this.successMessage = 'Compétence supprimée avec succès';
        this.clearMessagesAfterDelay();
      });
  }

  toggleEdit(): void {
    this.isEditing = !this.isEditing;
    this.clearMessages();
    if (this.isEditing) this.profileForm.enable();
    else {
      this.profileForm.disable();
      if (this.employee) {
        this.populateEmployeeForm(this.employee);
        this.populateSkillsForm();
      }
    }
  }

  onSubmit(): void {
    if (this.profileForm.invalid) {
      this.markFormGroupTouched(this.profileForm);
      this.errorMessage = 'Veuillez corriger les erreurs du formulaire';
      return;
    }

    this.saving = true;
    this.clearMessages();

    const formValue = this.profileForm.value;
    const employeeData = {
      name: formValue.name,
      position: formValue.position,
      email: formValue.email,
      hire_date: formValue.hire_date,
      phone: formValue.phone,
      gender: formValue.gender,
      location: formValue.location,
      notes: formValue.notes
    };

    // Mise à jour employé (sans skills)
    this.employeeService.updateEmployee(this.employeeId, employeeData)
      .pipe(
        catchError(err => {
          this.errorMessage = 'Erreur lors de la mise à jour du profil';
          console.error(err);
          return of(null);
        }),
        finalize(() => this.saving = false)
      )
      .subscribe(updatedEmployee => {
        if (updatedEmployee) {
          this.employee = updatedEmployee;
          this.successMessage = 'Profil mis à jour avec succès';
          this.updateEmployeeSkills();
          this.isEditing = false;
          this.profileForm.disable();
          this.clearMessagesAfterDelay();
        }
      });
  }

  private updateEmployeeSkills(): void {
    this.skillsFormArray.value.forEach((skill: any, index: number) => {
      const payload: EmployeeSkill = {
        employee_id: this.employeeId,
        skill_id: skill.skill_id,
        skill_level_id: skill.actual_skill_level_id,
        acquired_date: skill.acquired_date,
        certification: skill.certification,
        last_evaluated_date: skill.last_evaluated_date
      };

      if (skill.id) {
        this.skillService.updateEmployeeSkill(this.employeeId, skill.id, payload)
          .pipe(catchError(err => { console.error(err); return of(null); }))
          .subscribe();
      } else {
        this.skillService.createEmployeeSkill(payload)
          .pipe(catchError(err => { console.error(err); return of(null); }))
          .subscribe((created: any) => {
            if (created) this.skillsFormArray.at(index).patchValue({ id: created.id });
          });
      }
    });
  }

  getSkillName(skillId: string): string {
    const skill = this.skills.find(s => s.id === skillId);
    return skill ? skill.name : 'Compétence inconnue';
  }

  getSkillLevelName(levelId: string): string {
    const level = this.skillLevels.find(l => l.id === levelId);
    return level ? level.name : 'Niveau inconnu';
  }

  goBack(): void {
    this.router.navigate(['/employees']);
  }

  private markFormGroupTouched(formGroup: FormGroup): void {
    Object.keys(formGroup.controls).forEach(key => {
      const control = formGroup.get(key);
      control?.markAsTouched();
      if (control instanceof FormGroup) this.markFormGroupTouched(control);
      else if (control instanceof FormArray) control.controls.forEach(c => { if(c instanceof FormGroup) this.markFormGroupTouched(c); });
    });
  }

  private clearMessages(): void { this.errorMessage = ''; this.successMessage = ''; }
  private clearMessagesAfterDelay(delay = 3000): void { setTimeout(() => this.clearMessages(), delay); }
}
