import { Component, OnInit } from '@angular/core';
import { EmployeeSkill, EmployeeSkillService } from '../../services/employee-skill.service';

@Component({
  selector: 'app-employee-skills',
  templateUrl: './employee-skills.component.html',
  styleUrls: ['./employee-skills.component.css']
})
export class EmployeeSkillsComponent implements OnInit {
  skills: EmployeeSkill[] = [];
  loading = false;
  error = '';

  constructor(private skillService: EmployeeSkillService) {}

  ngOnInit(): void {
    this.loadSkills();
  }

  loadSkills(): void {
    this.loading = true;
    this.skillService.getAll().subscribe({
      next: (data) => { this.skills = data; this.loading = false; },
      error: (err) => { this.error = err.message; this.loading = false; }
    });
  }

  deleteSkill(employee_id: number, skill_id: number) {
    if (!confirm('Voulez-vous vraiment supprimer cette compétence ?')) return;

    this.skillService.delete(employee_id, skill_id).subscribe({
      next: () => this.loadSkills(),
      error: (err) => this.error = err.message
    });
  }
}
