import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { EmployeeSkillService, EmployeeSkill } from '../../services/employee-skill.service';

@Component({
  selector: 'app-employee-skills',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './employee-skills.component.html',
  styleUrls: ['./employee-skills.component.css']
})
export class EmployeeSkillsComponent implements OnInit {
  skills: EmployeeSkill[] = [];
  skillForm: FormGroup;
  showAddForm: boolean = false;
  editingSkill: EmployeeSkill | null = null;
  loading: boolean = false;
  errorMessage: string | null = null;

  constructor(
    private skillService: EmployeeSkillService,
    private fb: FormBuilder
  ) {
    this.skillForm = this.fb.group({
      employee_id: ['', Validators.required],
      skill_id: ['', Validators.required],
      actual_skill_level_id: ['', Validators.required],
      acquired_date: ['', Validators.required],
      certification: [''],
      last_evaluated_date: ['']
    });
  }

  ngOnInit(): void {
    this.loadSkills();
  }

  loadSkills(): void {
    this.loading = true;
    this.errorMessage = null;
    this.skillService.getAll().subscribe({
      next: (data: EmployeeSkill[]) => {
        this.skills = data;
        this.loading = false;
      },
      error: (err: any) => {
        this.errorMessage = err?.message || 'Erreur lors du chargement';
        this.loading = false;
      }
    });
  }

  onSubmit(): void {
    if (!this.skillForm.valid) return;
    const skillData: EmployeeSkill = this.skillForm.value;

    if (this.editingSkill) {
      this.skillService.update(this.editingSkill.employee_id, this.editingSkill.skill_id, skillData)
        .subscribe({
          next: (updatedSkill: EmployeeSkill) => {
            const index = this.skills.findIndex(s =>
              s.employee_id === updatedSkill.employee_id &&
              s.skill_id === updatedSkill.skill_id
            );
            if (index !== -1) this.skills[index] = updatedSkill;
            this.cancelEdit();
          },
          error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de la mise à jour'
        });
    } else {
      this.skillService.create(skillData)
        .subscribe({
          next: (newSkill: EmployeeSkill) => {
            this.skills.push(newSkill);
            this.cancelEdit();
          },
          error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de l\'ajout'
        });
    }
  }

  editSkill(skill: EmployeeSkill): void {
    this.editingSkill = skill;
    this.showAddForm = true;
    this.skillForm.patchValue(skill);
  }

  deleteSkill(skill: EmployeeSkill): void {
    if (!confirm('Voulez-vous vraiment supprimer cette compétence ?')) return;

    this.skillService.delete(skill.employee_id, skill.skill_id)
      .subscribe({
        next: () => this.skills = this.skills.filter(s =>
          !(s.employee_id === skill.employee_id && s.skill_id === skill.skill_id)
        ),
        error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de la suppression'
      });
  }

  cancelEdit(): void {
    this.editingSkill = null;
    this.showAddForm = false;
    this.skillForm.reset();
  }
}
<div class="container mx-auto p-4">
  <!-- Message d'erreur -->
  <div *ngIf="errorMessage" class="mb-4 p-3 bg-red-100 text-red-700 rounded-md">
    {{ errorMessage }}
  </div>

  <!-- Formulaire ajout / modification -->
  <div class="mb-6 p-4 border rounded shadow-sm">
    <h2 class="text-lg font-semibold mb-3">
      {{ editingSkill ? 'Modifier compétence' : 'Ajouter compétence' }}
    </h2>
    <form [formGroup]="skillForm" (ngSubmit)="onSubmit()">
      <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label class="block mb-1 font-medium">Employé ID</label>
          <input type="number" formControlName="employee_id" class="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label class="block mb-1 font-medium">Compétence ID</label>
          <input type="number" formControlName="skill_id" class="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label class="block mb-1 font-medium">Niveau actuel</label>
          <input type="number" formControlName="actual_skill_level_id" class="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label class="block mb-1 font-medium">Date acquisition</label>
          <input type="date" formControlName="acquired_date" class="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label class="block mb-1 font-medium">Certificat</label>
          <input type="text" formControlName="certification" class="w-full border rounded px-2 py-1" />
        </div>
        <div>
          <label class="block mb-1 font-medium">Dernière évaluation</label>
          <input type="date" formControlName="last_evaluated_date" class="w-full border rounded px-2 py-1" />
        </div>
      </div>

      <div class="mt-4 flex gap-2">
        <button type="submit" class="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          {{ editingSkill ? 'Mettre à jour' : 'Ajouter' }}
        </button>
        <button type="button" class="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500" (click)="cancelEdit()">
          Annuler
        </button>
      </div>
    </form>
  </div>

  <!-- Liste des compétences -->
  <div class="overflow-x-auto">
    <table class="min-w-full border border-gray-200">
      <thead class="bg-gray-100">
        <tr>
          <th class="p-2 border">Employé ID</th>
          <th class="p-2 border">Compétence ID</th>
          <th class="p-2 border">Niveau actuel</th>
          <th class="p-2 border">Date acquisition</th>
          <th class="p-2 border">Certificat</th>
          <th class="p-2 border">Dernière évaluation</th>
          <th class="p-2 border">Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let s of skills" class="hover:bg-gray-50">
          <td class="p-2 border">{{ s.employee_id }}</td>
          <td class="p-2 border">{{ s.skill_id }}</td>
          <td class="p-2 border">{{ s.actual_skill_level_id }}</td>
          <td class="p-2 border">{{ s.acquired_date | date:'dd/MM/yyyy' }}</td>
          <td class="p-2 border">{{ s.certification || '-' }}</td>
          <td class="p-2 border">{{ s.last_evaluated_date | date:'dd/MM/yyyy' }}</td>
          <td class="p-2 border flex gap-2">
            <button (click)="editSkill(s)" class="px-2 py-1 bg-yellow-400 text-white rounded hover:bg-yellow-500">Modifier</button>
            <button (click)="deleteSkill(s)" class="px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700">Supprimer</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
