import { Component, OnInit, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterLink } from '@angular/router';
import { EmployeeService } from '../../services/employee.service';
import { JobDescriptionService } from '../../services/job-description.service';
import { Employee } from '../../models/employee.model';
import { JobDescription } from '../../models/job-description.model';

interface OrgNode {
  id: number;
  name: string;
  position: string;
  email: string;
  phone?: string;
  jobDescriptionId: number;
  children: OrgNode[];
  level: number;
  department?: string;
  employeeCount: number;
  isVacant: boolean;
  employees: Employee[];
}

interface OrgStats {
  totalEmployees: number;
  totalPositions: number;
  vacantPositions: number;
  departmentStats: { [key: string]: number };
}

@Component({
  selector: 'app-organigramme',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterLink],
  templateUrl: './organigramme.component.html',
  styleUrls: ['./organigramme.component.css']
})
export class OrganigrammeComponent implements OnInit, AfterViewInit {
  @ViewChild('orgChart', { static: false }) orgChartRef!: ElementRef<HTMLDivElement>;

  employees: Employee[] = [];
  jobDescriptions: JobDescription[] = [];
  orgChart: OrgNode[] = [];
  filteredOrgChart: OrgNode[] = [];
  orgStats: OrgStats = {
    totalEmployees: 0,
    totalPositions: 0,
    vacantPositions: 0,
    departmentStats: {}
  };

  // UI State
  loading: boolean = true;
  errorMessage: string | null = null;
  viewMode: 'visual' | 'tree' = 'visual';

  // Filters and Search
  searchQuery: string = '';
  selectedDepartment: string = '';
  selectedLevel: number | null = null;
  showVacantOnly: boolean = false;

  // Interactive features
  selectedNode: OrgNode | null = null;
  showNodeDetails: boolean = false;
  expandedNodes: Set<number> = new Set();

  // Zoom and Pan
  zoomLevel: number = 1;
  panX: number = 0;
  panY: number = 0;

  // Available filters
  departments: string[] = [];
  maxLevel: number = 0;

  constructor(
    private employeeService: EmployeeService,
    private jobDescriptionService: JobDescriptionService
  ) { }

  ngOnInit(): void {
    this.loadData();
  }

  ngAfterViewInit(): void {
    this.setupInteractivity();
  }

  loadData(): void {
    this.loading = true;
    this.errorMessage = null;

    Promise.all([
      this.employeeService.getEmployees().toPromise(),
      this.jobDescriptionService.getJobDescriptions().toPromise()
    ]).then(([employees, jobDescriptions]) => {
      this.employees = employees || [];
      this.jobDescriptions = jobDescriptions || [];
      this.buildOrgChart();
      this.calculateStats();
      this.extractFilterOptions();
      this.applyFilters();
      this.loading = false;
    }).catch(err => {
      console.error('Error loading organizational data:', err);
      this.errorMessage = 'Erreur lors du chargement des donnÃ©es organisationnelles.';
      this.loading = false;
    });
  }

  buildOrgChart(): void {
    const jobMap = new Map<number, JobDescription>();
    this.jobDescriptions.forEach(job => {
      if (job.id) {
        jobMap.set(job.id, job);
      }
    });

    const employeesByJob = new Map<number, Employee[]>();
    this.employees.forEach(emp => {
      if (emp.job_description_id) {
        if (!employeesByJob.has(emp.job_description_id)) {
          employeesByJob.set(emp.job_description_id, []);
        }
        employeesByJob.get(emp.job_description_id)!.push(emp);
      }
    });

    const rootNodes: OrgNode[] = [];
    const processedJobs = new Set<number>();

    // Trouver les postes de direction (sans supÃ©rieur)
    this.jobDescriptions.forEach(job => {
      if (job.id && !job.superieur_n1 && !processedJobs.has(job.id)) {
        const node = this.createOrgNode(job, employeesByJob, jobMap, processedJobs, 0);
        if (node) {
          rootNodes.push(node);
        }
      }
    });

    // Si aucun poste de direction, crÃ©er des nÅ“uds pour tous les postes
    if (rootNodes.length === 0) {
      this.jobDescriptions.forEach(job => {
        if (job.id && !processedJobs.has(job.id)) {
          const node = this.createOrgNode(job, employeesByJob, jobMap, processedJobs, 0);
          if (node) {
            rootNodes.push(node);
          }
        }
      });
    }

    this.orgChart = rootNodes;
  }

  createOrgNode(
    job: JobDescription, 
    employeesByJob: Map<number, Employee[]>,
    jobMap: Map<number, JobDescription>,
    processedJobs: Set<number>,
    level: number
  ): OrgNode | null {
    if (!job.id || processedJobs.has(job.id)) {
      return null;
    }

    processedJobs.add(job.id);

    const employeesForJob = employeesByJob.get(job.id) || [];
    const mainEmployee = employeesForJob[0];

    const node: OrgNode = {
      id: job.id,
      name: mainEmployee ? mainEmployee.name : 'Poste vacant',
      position: job.emploi,
      email: mainEmployee ? mainEmployee.email : '',
      phone: mainEmployee ? mainEmployee.phone : '',
      jobDescriptionId: job.id,
      children: [],
      level: level,
      department: job.filiere_activite,
      employeeCount: employeesForJob.length,
      isVacant: employeesForJob.length === 0,
      employees: employeesForJob
    };

    // Trouver les postes subordonnÃ©s
    this.jobDescriptions.forEach(subordinateJob => {
      if (subordinateJob.superieur_n1 === job.id || subordinateJob.superieur_n2 === job.id) {
        const childNode = this.createOrgNode(subordinateJob, employeesByJob, jobMap, processedJobs, level + 1);
        if (childNode) {
          node.children.push(childNode);
        }
      }
    });

    return node;
  }

  calculateStats(): void {
    let totalEmployees = 0;
    let totalPositions = 0;
    let vacantPositions = 0;
    const departmentStats: { [key: string]: number } = {};

    const countNodes = (nodes: OrgNode[]) => {
      nodes.forEach(node => {
        totalPositions++;
        totalEmployees += node.employeeCount;

        if (node.isVacant) {
          vacantPositions++;
        }

        if (node.department) {
          departmentStats[node.department] = (departmentStats[node.department] || 0) + node.employeeCount;
        }

        countNodes(node.children);
      });
    };

    countNodes(this.orgChart);

    this.orgStats = {
      totalEmployees,
      totalPositions,
      vacantPositions,
      departmentStats
    };
  }

  extractFilterOptions(): void {
    const departments = new Set<string>();
    let maxLevel = 0;

    const extractFromNodes = (nodes: OrgNode[]) => {
      nodes.forEach(node => {
        if (node.department) {
          departments.add(node.department);
        }
        maxLevel = Math.max(maxLevel, node.level);
        extractFromNodes(node.children);
      });
    };

    extractFromNodes(this.orgChart);

    this.departments = Array.from(departments).sort();
    this.maxLevel = maxLevel;
  }

  applyFilters(): void {
    this.filteredOrgChart = this.filterNodes(this.orgChart);
  }

  filterNodes(nodes: OrgNode[]): OrgNode[] {
    return nodes.filter(node => {
      // Search filter
      const matchesSearch = !this.searchQuery || 
        node.name.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
        node.position.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
        (node.department && node.department.toLowerCase().includes(this.searchQuery.toLowerCase()));

      // Department filter
      const matchesDepartment = !this.selectedDepartment || node.department === this.selectedDepartment;

      // Level filter
      const matchesLevel = this.selectedLevel === null || node.level === this.selectedLevel;

      // Vacant filter
      const matchesVacant = !this.showVacantOnly || node.isVacant;

      const matches = matchesSearch && matchesDepartment && matchesLevel && matchesVacant;

      if (matches) {
        // Filter children recursively
        node.children = this.filterNodes(node.children);
        return true;
      }

      // Check if any children match
      const filteredChildren = this.filterNodes(node.children);
      if (filteredChildren.length > 0) {
        node.children = filteredChildren;
        return true;
      }

      return false;
    });
  }

  onSearchChange(): void {
    this.applyFilters();
  }

  onFilterChange(): void {
    this.applyFilters();
  }

  clearFilters(): void {
    this.searchQuery = '';
    this.selectedDepartment = '';
    this.selectedLevel = null;
    this.showVacantOnly = false;
    this.applyFilters();
  }

  switchView(mode: 'visual' | 'tree'): void {
    this.viewMode = mode;
    this.selectedNode = null;
    this.showNodeDetails = false;
  }

  selectNode(node: OrgNode): void {
    this.selectedNode = node;
    this.showNodeDetails = true;
  }

  closeNodeDetails(): void {
    this.selectedNode = null;
    this.showNodeDetails = false;
  }

  toggleNodeExpansion(nodeId: number): void {
    if (this.expandedNodes.has(nodeId)) {
      this.expandedNodes.delete(nodeId);
    } else {
      this.expandedNodes.add(nodeId);
    }
  }

  isNodeExpanded(nodeId: number): boolean {
    return this.expandedNodes.has(nodeId);
  }

  // Zoom and Pan functionality
  zoomIn(): void {
    this.zoomLevel = Math.min(this.zoomLevel * 1.2, 3);
    this.updateTransform();
  }

  zoomOut(): void {
    this.zoomLevel = Math.max(this.zoomLevel / 1.2, 0.3);
    this.updateTransform();
  }

  resetZoom(): void {
    this.zoomLevel = 1;
    this.panX = 0;
    this.panY = 0;
    this.updateTransform();
  }

  private updateTransform(): void {
    if (this.orgChartRef) {
      const element = this.orgChartRef.nativeElement.querySelector('.org-chart-content') as HTMLElement;
      if (element) {
        element.style.transform = `translate(${this.panX}px, ${this.panY}px) scale(${this.zoomLevel})`;
      }
    }
  }

  private setupInteractivity(): void {
    if (this.orgChartRef) {
      const element = this.orgChartRef.nativeElement;
      let isDragging = false;
      let lastX = 0;
      let lastY = 0;

      element.addEventListener('mousedown', (e) => {
        if (e.target === element || (e.target as HTMLElement).classList.contains('org-chart-content')) {
          isDragging = true;
          lastX = e.clientX;
          lastY = e.clientY;
          element.style.cursor = 'grabbing';
        }
      });

      element.addEventListener('mousemove', (e) => {
        if (isDragging) {
          const deltaX = e.clientX - lastX;
          const deltaY = e.clientY - lastY;
          this.panX += deltaX;
          this.panY += deltaY;
          this.updateTransform();
          lastX = e.clientX;
          lastY = e.clientY;
        }
      });

      element.addEventListener('mouseup', () => {
        isDragging = false;
        element.style.cursor = 'grab';
      });

      element.addEventListener('wheel', (e) => {
        e.preventDefault();
        if (e.deltaY < 0) {
          this.zoomIn();
        } else {
          this.zoomOut();
        }
      });
    }
  }

  getNodeClass(node: OrgNode): string {
    const baseClass = 'org-node';
    const levelClass = `level-${Math.min(node.level, 4)}`;
    const vacantClass = node.isVacant ? 'vacant' : '';
    const selectedClass = this.selectedNode?.id === node.id ? 'selected' : '';

    return `${baseClass} ${levelClass} ${vacantClass} ${selectedClass}`.trim();
  }

  getNodeIcon(node: OrgNode): string {
    if (node.isVacant) return 'ðŸ‘¤';
    if (node.level === 0) return 'ðŸ‘‘';
    if (node.level === 1) return 'ðŸŽ¯';
    if (node.level === 2) return 'ðŸ“Š';
    return 'ðŸ‘¨â€ðŸ’¼';
  }

  exportOrgChart(format: 'png' | 'pdf' = 'png'): void {
    // Implementation for export functionality
    console.log(`Exporting organigramme as ${format}`);
    // This would typically use libraries like html2canvas or jsPDF
  }

  printOrgChart(): void {
    window.print();
  }
}
