import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface EmployeeSkill {
  employee_id: number;
  skill_id: number;
  actual_skill_level_id?: number;
  acquired_date?: string;
  certification?: string;
  last_evaluated_date?: string;
  Employee?: any;
  Skill?: any;
  SkillLevel?: any;
}

@Injectable({
  providedIn: 'root'
})
export class EmployeeSkillService {
  private apiUrl = 'http://localhost:3000/employee-skills'; // ton endpoint Node.js

  constructor(private http: HttpClient) {}

  getAll(): Observable<EmployeeSkill[]> {
    return this.http.get<EmployeeSkill[]>(this.apiUrl);
  }

  delete(employee_id: number, skill_id: number): Observable<any> {
    return this.http.delete(`${this.apiUrl}/${employee_id}/${skill_id}`);
  }

  // <- Ajout de la méthode create
  create(skill: Partial<EmployeeSkill>): Observable<EmployeeSkill> {
    return this.http.post<EmployeeSkill>(this.apiUrl, skill);
  }
}
