      next: employeeSkills => {
        this.employeeSkills = employeeSkills;
        this.loadingSkills = false;
      },
      error: err => {
        console.error('Error loading employee skills:', err);
        this.loadingSkills = false;
      }
    });
  }
  loadSkillsData() {
