import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { EmployeeService } from '../../services/employee.service';
import type { Employee } from '../../models/employee.model';

@Component({
  selector: 'app-employees',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './employees.component.html',
  styleUrls: ['./employees.component.css']
})
export class EmployeesComponent implements OnInit {
  employees: Employee[] = [];
  employeeForm: FormGroup;
  showAddForm: boolean = false;
  editingEmployee: Employee | null = null;
  loading: boolean = false;
  errorMessage: string | null = null;

  constructor(
    private employeeService: EmployeeService,
    private fb: FormBuilder
  ) {
    this.employeeForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      position: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      hire_date: ['', Validators.required],
      phone: [''],
      gender: [''],
      location: [''],
      notes: ['']
    });
  }

  ngOnInit(): void {
    this.loadEmployees();
  }

  loadEmployees(): void {
    this.loading = true;
    this.errorMessage = null;
    this.employeeService.getEmployees().subscribe({
      next: (data) => {
        this.employees = data;
        this.loading = false;
      },
      error: (err) => {
        console.error(err);
        this.errorMessage = 'Erreur lors du chargement des employés';
        this.loading = false;
      }
    });
  }

  onSubmit(): void {
    if (!this.employeeForm.valid) return;

    const employeeData = this.employeeForm.value;
    if (this.editingEmployee) {
      this.employeeService.updateEmployee(this.editingEmployee.id!, employeeData).subscribe({
        next: (updated) => {
          const index = this.employees.findIndex(emp => emp.id === updated.id);
          if (index !== -1) this.employees[index] = updated;
          this.cancelEdit();
        },
        error: (err) => this.errorMessage = 'Erreur lors de la mise à jour'
      });
    } else {
      this.employeeService.createEmployee(employeeData).subscribe({
        next: (newEmp) => {
          this.employees.push(newEmp);
          this.cancelEdit();
        },
        error: (err) => this.errorMessage = 'Erreur lors de la création'
      });
    }
  }

  editEmployee(emp: Employee): void {
    this.editingEmployee = emp;
    this.showAddForm = true;
    this.employeeForm.patchValue({
      name: emp.name,
      position: emp.position,
      email: emp.email,
      hire_date: emp.hire_date ? emp.hire_date.split('T')[0] : '',
      phone: emp.phone || '',
      gender: emp.gender || '',
      location: emp.location || '',
      notes: emp.notes || ''
    });
  }

  deleteEmployee(emp: Employee): void {
    if (!confirm(`Supprimer ${emp.name} ?`)) return;
    this.employeeService.deleteEmployee(emp.id!).subscribe({
      next: () => this.employees = this.employees.filter(e => e.id !== emp.id),
      error: (err) => this.errorMessage = 'Erreur lors de la suppression'
    });
  }

  cancelEdit(): void {
    this.editingEmployee = null;
    this.showAddForm = false;
    this.employeeForm.reset();
  }
}
<div class="container mx-auto p-4">
  <h2 class="text-lg font-semibold mb-4">Gestion des employés</h2>

  <!-- Message d'erreur -->
  <div *ngIf="errorMessage" class="mb-4 p-3 bg-red-100 text-red-700 rounded">
    {{ errorMessage }}
  </div>

  <!-- Formulaire -->
  <div class="mb-6 p-4 border rounded shadow-sm">
    <form [formGroup]="employeeForm" (ngSubmit)="onSubmit()">
      <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input formControlName="name" placeholder="Nom" class="border px-2 py-1" />
        <input formControlName="position" placeholder="Poste" class="border px-2 py-1" />
        <input formControlName="email" placeholder="Email" type="email" class="border px-2 py-1" />
        <input formControlName="hire_date" type="date" class="border px-2 py-1" />
        <input formControlName="phone" placeholder="Téléphone" class="border px-2 py-1" />
        <input formControlName="gender" placeholder="Genre" class="border px-2 py-1" />
        <input formControlName="location" placeholder="Localisation" class="border px-2 py-1" />
        <input formControlName="notes" placeholder="Notes" class="border px-2 py-1" />
      </div>
      <div class="mt-4 flex gap-2">
        <button type="submit" class="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          {{ editingEmployee ? 'Mettre à jour' : 'Ajouter' }}
        </button>
        <button type="button" (click)="cancelEdit()" class="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500">
          Annuler
        </button>
      </div>
    </form>
  </div>

  <!-- Liste -->
  <table class="min-w-full border border-gray-200">
    <thead class="bg-gray-100">
      <tr>
        <th class="p-2 border">Nom</th>
        <th class="p-2 border">Poste</th>
        <th class="p-2 border">Email</th>
        <th class="p-2 border">Date embauche</th>
        <th class="p-2 border">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let emp of employees" class="hover:bg-gray-50">
        <td class="p-2 border">{{ emp.name }}</td>
        <td class="p-2 border">{{ emp.position }}</td>
        <td class="p-2 border">{{ emp.email }}</td>
        <td class="p-2 border">{{ emp.hire_date | date:'dd/MM/yyyy' }}</td>
        <td class="p-2 border flex gap-2">
          <button (click)="editEmployee(emp)" class="px-2 py-1 bg-yellow-400 text-white rounded hover:bg-yellow-500">Modifier</button>
          <button (click)="deleteEmployee(emp)" class="px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700">Supprimer</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
