import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';

// Interface pour un EmployeeSkill
export interface EmployeeSkill {
  employee_id: number;
  skill_id: number;
  actual_skill_level_id?: number;
  acquired_date?: string; // ou Date
  last_evaluated_date?: string; // ou Date
  certification?: string;
  Employee?: { name: string };
  Skill?: { name: string };
  SkillLevel?: { name: string };
}

@Injectable({
  providedIn: 'root'
})
export class EmployeeSkillService {

  private apiUrl = 'http://localhost:3000/api/employeeskills'; // à adapter à ton backend

  constructor(private http: HttpClient) { }

  // Récupérer toutes les compétences des employés
  getAll(): Observable<EmployeeSkill[]> {
    return this.http.get<EmployeeSkill[]>(this.apiUrl)
      .pipe(
        catchError(this.handleError)
      );
  }

  // Supprimer une compétence pour un employé
  delete(employee_id: number, skill_id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${employee_id}/${skill_id}`)
      .pipe(
        catchError(this.handleError)
      );
  }

  // Gestion centralisée des erreurs
  private handleError(error: HttpErrorResponse) {
    let errorMsg = '';
    if (error.error instanceof ErrorEvent) {
      // Erreur côté client
      errorMsg = `Erreur client: ${error.error.message}`;
    } else {
      // Erreur côté serveur
      errorMsg = `Erreur serveur [${error.status}]: ${error.message}`;
    }
    return throwError(() => new Error(errorMsg));
  }
}
