import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { EmployeeSkillService } from '../../services/employee-skill.service';
import { EmployeeService, Employee } from '../../services/employee.service';
import { SkillService, Skill, SkillLevel } from '../../services/skill.service';
import { EmployeeSkill } from '../../models/employee-skill.model';

@Component({
  selector: 'app-employee-skills',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './employee-skills.component.html',
  styleUrls: ['./employee-skills.component.css']
})
export class EmployeeSkillsComponent implements OnInit {
  skills: EmployeeSkill[] = [];
  employees: Employee[] = [];
  skillsList: Skill[] = [];
  skillLevels: SkillLevel[] = [];
  skillForm: FormGroup;
  showAddForm: boolean = false;
  editingSkill: EmployeeSkill | null = null;
  loading: boolean = false;
  errorMessage: string | null = null;

  constructor(
    private skillService: EmployeeSkillService,
    private employeeService: EmployeeService,
    private skillMetaService: SkillService,
    private fb: FormBuilder
  ) {
    this.skillForm = this.fb.group({
      employee_id: ['', Validators.required],
      skill_id: ['', Validators.required],
      actual_skill_level_id: ['', Validators.required],
      acquired_date: ['', Validators.required],
      certification: [''],
      last_evaluated_date: ['']
    });
  }

  ngOnInit(): void {
    this.loadSkills();
    this.loadEmployees();
    this.loadSkillList();
    this.loadSkillLevels();
  }

  loadSkills(): void {
    this.loading = true;
    this.errorMessage = null;
    this.skillService.getAll().subscribe({
      next: (data: EmployeeSkill[]) => {
        this.skills = data;
        this.loading = false;
      },
      error: (err: any) => {
        this.errorMessage = err?.message || 'Erreur lors du chargement';
        this.loading = false;
      }
    });
  }

  loadEmployees(): void {
    this.employeeService.getEmployees().subscribe({
      next: (data: Employee[]) => this.employees = data,
      error: (err: any) => console.error(err)
    });
  }

  loadSkillList(): void {
    this.skillMetaService.getSkills().subscribe({
      next: (data: Skill[]) => this.skillsList = data,
      error: (err: any) => console.error(err)
    });
  }

  loadSkillLevels(): void {
    this.skillMetaService.getSkillLevels().subscribe({
      next: (data: SkillLevel[]) => this.skillLevels = data,
      error: (err: any) => console.error(err)
    });
  }

  onSubmit(): void {
    if (!this.skillForm.valid) return;
    const skillData: EmployeeSkill = this.skillForm.value;

    if (this.editingSkill) {
      this.skillService.update(this.editingSkill.employee_id, this.editingSkill.skill_id, skillData)
        .subscribe({
          next: (updatedSkill: EmployeeSkill) => {
            const index = this.skills.findIndex(s =>
              s.employee_id === updatedSkill.employee_id &&
              s.skill_id === updatedSkill.skill_id
            );
            if (index !== -1) this.skills[index] = updatedSkill;
            this.cancelEdit();
          },
          error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de la mise à jour'
        });
    } else {
      this.skillService.create(skillData)
        .subscribe({
          next: (newSkill: EmployeeSkill) => {
            this.skills.push(newSkill);
            this.cancelEdit();
          },
          error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de l\'ajout'
        });
    }
  }

  editSkill(skill: EmployeeSkill): void {
    this.editingSkill = skill;
    this.showAddForm = true;
    this.skillForm.patchValue({
      employee_id: skill.employee_id,
      skill_id: skill.skill_id,
      actual_skill_level_id: skill.actual_skill_level_id,
      acquired_date: skill.acquired_date,
      certification: skill.certification,
      last_evaluated_date: skill.last_evaluated_date
    });
  }

  deleteSkill(skill: EmployeeSkill): void {
    if (!confirm('Voulez-vous vraiment supprimer cette compétence ?')) return;

    this.skillService.delete(skill.employee_id, skill.skill_id)
      .subscribe({
        next: () => this.skills = this.skills.filter(s =>
          !(s.employee_id === skill.employee_id && s.skill_id === skill.skill_id)
        ),
        error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de la suppression'
      });
  }

  cancelEdit(): void {
    this.editingSkill = null;
    this.showAddForm = false;
    this.skillForm.reset();
  }
}
<div class="container mx-auto p-4">
  <button (click)="showAddForm = !showAddForm" class="mb-4 px-4 py-2 bg-blue-500 text-white rounded">
    {{ showAddForm ? 'Fermer' : 'Ajouter une compétence' }}
  </button>

  <div *ngIf="showAddForm" class="mb-6 p-4 border rounded bg-gray-50">
    <form [formGroup]="skillForm" (ngSubmit)="onSubmit()">
      <div class="mb-2">
        <label>Employé:</label>
        <select formControlName="employee_id" class="border p-1 w-full">
          <option [ngValue]="null">Sélectionner</option>
          <option *ngFor="let emp of employees" [ngValue]="emp.id">{{ emp.name }}</option>
        </select>
      </div>
      <div class="mb-2">
        <label>Compétence:</label>
        <select formControlName="skill_id" class="border p-1 w-full">
          <option [ngValue]="null">Sélectionner</option>
          <option *ngFor="let skill of skillsList" [ngValue]="skill.id">{{ skill.name }}</option>
        </select>
      </div>
      <div class="mb-2">
        <label>Niveau actuel:</label>
        <select formControlName="actual_skill_level_id" class="border p-1 w-full">
          <option [ngValue]="null">Sélectionner</option>
          <option *ngFor="let lvl of skillLevels" [ngValue]="lvl.id">{{ lvl.name }}</option>
        </select>
      </div>
      <div class="mb-2">
        <label>Date d'acquisition:</label>
        <input type="date" formControlName="acquired_date" class="border p-1 w-full"/>
      </div>
      <div class="mb-2">
        <label>Certification:</label>
        <input type="text" formControlName="certification" class="border p-1 w-full"/>
      </div>
      <div class="mb-2">
        <label>Dernière évaluation:</label>
        <input type="date" formControlName="last_evaluated_date" class="border p-1 w-full"/>
      </div>
      <button type="submit" class="px-4 py-2 bg-green-500 text-white rounded">
        {{ editingSkill ? 'Modifier' : 'Ajouter' }}
      </button>
      <button type="button" (click)="cancelEdit()" class="ml-2 px-4 py-2 bg-gray-500 text-white rounded">Annuler</button>
    </form>
  </div>

  <table class="table-auto w-full border-collapse">
    <thead>
      <tr class="bg-gray-200">
        <th class="border p-2">Employé</th>
        <th class="border p-2">Compétence</th>
        <th class="border p-2">Niveau</th>
        <th class="border p-2">Acquisition</th>
        <th class="border p-2">Certification</th>
        <th class="border p-2">Dernière évaluation</th>
        <th class="border p-2">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let s of skills" class="hover:bg-gray-50">
        <td class="border p-2">{{ getEmployeeName(s.employee_id) }}</td>
        <td class="border p-2">{{ getSkillName(s.skill_id) }}</td>
        <td class="border p-2">{{ getSkillLevelName(s.actual_skill_level_id) }}</td>
        <td class="border p-2">{{ s.acquired_date }}</td>
        <td class="border p-2">{{ s.certification }}</td>
        <td class="border p-2">{{ s.last_evaluated_date }}</td>
        <td class="border p-2">
          <button (click)="editSkill(s)" class="px-2 py-1 bg-yellow-400 text-white rounded hover:bg-yellow-500">Modifier</button>
          <button (click)="deleteSkill(s)" class="px-2 py-1 bg-red-600 text-white rounded hover:bg-red-700">Supprimer</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
