import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { EmployeeSkillService } from '../../services/employee-skill.service';
import { EmployeeService, Employee } from '../../services/employee.service';
import { SkillService, Skill, SkillLevel } from '../../services/skill.service';
import { EmployeeSkill } from '../../models/employee-skill.model';

@Component({
  selector: 'app-employee-skills',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './employee-skills.component.html',
  styleUrls: ['./employee-skills.component.css']
})
export class EmployeeSkillsComponent implements OnInit {
  skills: EmployeeSkill[] = [];
  employees: Employee[] = [];
  skillsList: Skill[] = [];
  skillLevels: SkillLevel[] = [];
  skillForm: FormGroup;
  showAddForm: boolean = false;
  editingSkill: EmployeeSkill | null = null;
  loading: boolean = false;
  errorMessage: string | null = null;

  constructor(
    private skillService: EmployeeSkillService,
    private employeeService: EmployeeService,
    private skillMetaService: SkillService,
    private fb: FormBuilder
  ) {
    this.skillForm = this.fb.group({
      employee_id: ['', Validators.required],
      skill_id: ['', Validators.required],
      actual_skill_level_id: [null, Validators.required], // objet SkillLevel complet
      acquired_date: ['', Validators.required],
      certification: [''],
      last_evaluated_date: ['']
    });
  }

  ngOnInit(): void {
    this.loadSkillLevels(); // Charger les niveaux en premier pour associer ensuite
    this.loadEmployees();
    this.loadSkillList();
    this.loadSkills();
  }

  loadSkills(): void {
    this.loading = true;
    this.errorMessage = null;
    this.skillService.getAll().subscribe({
      next: (data: EmployeeSkill[]) => {
        this.skills = data.map(s => ({
          ...s,
          SkillLevel: this.skillLevels.find(l => l.id === s.actual_skill_level_id) // undefined si non trouvé
        }));
        this.loading = false;
      },
      error: (err: any) => {
        this.errorMessage = err?.message || 'Erreur lors du chargement';
        this.loading = false;
      }
    });
  }

  loadEmployees(): void {
    this.employeeService.getEmployees().subscribe({
      next: (data: Employee[]) => this.employees = data,
      error: (err: any) => console.error(err)
    });
  }

  loadSkillList(): void {
    this.skillMetaService.getSkills().subscribe({
      next: (data: Skill[]) => this.skillsList = data,
      error: (err: any) => console.error(err)
    });
  }

  loadSkillLevels(): void {
    this.skillMetaService.getSkillLevels().subscribe({
      next: (data: SkillLevel[]) => {
        this.skillLevels = data;
        // mettre à jour les skills déjà chargés
        this.skills = this.skills.map(s => ({
          ...s,
          SkillLevel: this.skillLevels.find(l => l.id === s.actual_skill_level_id) // undefined si non trouvé
        }));
      },
      error: (err: any) => console.error(err)
    });
  }

  getSkillLevelName(id: number | undefined): string {
    if (!id) return 'Niveau inconnu';
    const level = this.skillLevels.find((l) => l.id === id);
    return level ? level.name : 'Niveau inconnu';
  }

  onSubmit(): void {
    if (!this.skillForm.valid) return;

    const formValue = this.skillForm.value;
    const skillData: EmployeeSkill = {
      ...formValue,
      actual_skill_level_id: formValue.actual_skill_level_id?.id // envoyer seulement l'ID
    };

    if (this.editingSkill) {
      this.skillService.update(this.editingSkill.employee_id, this.editingSkill.skill_id, skillData)
        .subscribe({
          next: (updatedSkill: EmployeeSkill) => {
            // mettre à jour SkillLevel pour affichage
            updatedSkill.SkillLevel = this.skillLevels.find(l => l.id === updatedSkill.actual_skill_level_id);
            const index = this.skills.findIndex(s =>
              s.employee_id === updatedSkill.employee_id &&
              s.skill_id === updatedSkill.skill_id
            );
            if (index !== -1) this.skills[index] = updatedSkill;
            this.cancelEdit();
          },
          error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de la mise à jour'
        });
    } else {
      this.skillService.create(skillData)
        .subscribe({
          next: (newSkill: EmployeeSkill) => {
            newSkill.SkillLevel = this.skillLevels.find(l => l.id === newSkill.actual_skill_level_id);
            this.skills.push(newSkill);
            this.cancelEdit();
          },
          error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de l\'ajout'
        });
    }
  }

  editSkill(skill: EmployeeSkill): void {
    this.editingSkill = skill;
    this.showAddForm = true;

    const selectedLevel = this.skillLevels.find(l => l.id === skill.actual_skill_level_id);

    this.skillForm.patchValue({
      employee_id: skill.employee_id,
      skill_id: skill.skill_id,
      actual_skill_level_id: selectedLevel, // objet complet
      acquired_date: skill.acquired_date,
      certification: skill.certification,
      last_evaluated_date: skill.last_evaluated_date
    });
  }

  deleteSkill(skill: EmployeeSkill): void {
    if (!confirm('Voulez-vous vraiment supprimer cette compétence ?')) return;

    this.skillService.delete(skill.employee_id, skill.skill_id)
      .subscribe({
        next: () => this.skills = this.skills.filter(s =>
          !(s.employee_id === skill.employee_id && s.skill_id === skill.skill_id)
        ),
        error: (err: any) => this.errorMessage = err?.message || 'Erreur lors de la suppression'
      });
  }

  cancelEdit(): void {
    this.editingSkill = null;
    this.showAddForm = false;
    this.skillForm.reset();
  }
}
<div class="p-6">
  <h2 class="text-3xl font-bold text-primary mb-6">Liste des Compétences</h2>

  <!-- Bouton ajout compétence -->
  <div class="mb-4">
    <button (click)="showAddSkillForm = !showAddSkillForm" class="btn btn-primary">
      {{ showAddSkillForm ? 'Annuler' : 'Ajouter une compétence' }}
    </button>
  </div>

  <!-- Formulaire ajout / modification compétence -->
  <div *ngIf="(showAddSkillForm || editingSkill)" class="card mb-6 p-6">
    <h3 class="text-xl font-semibold text-dark mb-4">
      {{ editingSkill ? 'Modifier compétence' : 'Ajouter compétence' }}
    </h3>
    <form [formGroup]="skillForm" (ngSubmit)="onSubmit()">
      <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Employé *</label>
          <select formControlName="employee_id" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
            <option value="">Sélectionner un employé</option>
            <option *ngFor="let e of employees" [value]="e.id">{{ e.name }}</option>
          </select>
        </div>

        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Compétence *</label>
          <select formControlName="skill_id" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
            <option value="">Sélectionner une compétence</option>
            <option *ngFor="let sk of skillsList" [value]="sk.id">{{ sk.name }}</option>
          </select>
        </div>

        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Niveau actuel</label>
          <select formControlName="actual_skill_level_id" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
            <option [ngValue]="null">Sélectionner niveau</option>
            <option *ngFor="let level of skillLevels" [ngValue]="level">{{ level.level_name }}</option>
          </select>
        </div>

        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Date acquisition</label>
          <input type="date" formControlName="acquired_date" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>

        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Certificat</label>
          <input type="text" formControlName="certification" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>

        <div>
          <label class="block text-gray-700 text-sm font-bold mb-2">Dernière évaluation</label>
          <input type="date" formControlName="last_evaluated_date" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-primary">
        </div>
      </div>

      <div class="mt-6 flex gap-2">
        <button type="submit" [disabled]="!skillForm.valid" class="btn btn-primary disabled:opacity-50 disabled:cursor-not-allowed">
          {{ editingSkill ? 'Mettre à jour' : 'Ajouter' }}
        </button>
        <button type="button" (click)="cancelEdit()" class="btn btn-secondary">
          Annuler
        </button>
      </div>
    </form>
  </div>

  <!-- Liste des compétences -->
  <div class="card">
    <div class="overflow-x-auto">
      <table class="min-w-full table-auto">
        <thead class="bg-gray-50">
          <tr>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Employé</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Compétence</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Niveau actuel</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date acquisition</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Certificat</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dernière évaluation</th>
            <th class="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
          </tr>
        </thead>
        <tbody class="bg-white divide-y divide-gray-200">
          <tr *ngFor="let s of skills" class="hover:bg-gray-50">
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{{ s.Employee?.name }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{{ s.Skill?.name }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{{ s.SkillLevel?.level_name }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{{ s.acquired_date | date:'dd/MM/yyyy' }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{{ s.certification || '-' }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{{ s.last_evaluated_date | date:'dd/MM/yyyy' }}</td>
            <td class="px-6 py-4 whitespace-nowrap text-sm font-medium flex gap-2">
              <button (click)="editSkill(s)" class="text-primary hover:text-blue-700">Modifier</button>
              <button (click)="deleteSkill(s)" class="text-red-600 hover:text-red-900">Supprimer</button>
            </td>
          </tr>
          <tr *ngIf="skills.length === 0">
            <td colspan="7" class="text-center py-4 text-gray-600">Aucune compétence trouvée.</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
