    this.employeeId = +idParam;
    this.initializeForm();
    this.loadInitialData();
  }
  initializeForm() {
    this.profileForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(2)]],
      position: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      hire_date: ['', Validators.required],
      phone: [''],
      gender: [''],
      location: [''],
      notes: [''],
      skills: this.fb.array([])
    });
    this.profileForm.disable();
  }
  loadInitialData() {
    this.loading = true;
    this.clearMessages();
    Promise.all([this.loadEmployee(), this.loadSkillsData()]).finally(() => this.loading = false);
  }
  loadEmployee() {
    return new Promise(resolve => {
      this.employeeService.getEmployeeById(this.employeeId).pipe(catchError(err => {
        this.errorMessage = 'Erreur lors du chargement des données employé';
        console.error(err);
        return of(null);
      })).subscribe(employee => {
        if (employee) {
          this.employee = employee;
          this.populateEmployeeForm(employee);
          this.employeeSkills = employee.skills || [];
          this.populateSkillsForm();
        }
        resolve();
      });
    });
  }
  loadSkillsData() {
    return new Promise(resolve => {
      Promise.all([this.skillService.getSkills().toPromise(), this.skillService.getSkillTypes().toPromise(), this.skillService.getSkillLevels().toPromise()]).then(([skills, skillTypes, skillLevels]) => {
        this.skills = skills || [];
        this.skillTypes = skillTypes || [];
        this.skillLevels = skillLevels || [];
        resolve();
      }).catch(err => {
        this.errorMessage = 'Erreur lors du chargement des compétences';
        console.error(err);
        resolve();
      });
    });
  }
  populateEmployeeForm(employee) {
    this.profileForm.patchValue({
      name: employee.name,
      position: employee.position,
      email: employee.email,
      hire_date: employee.hire_date,
      phone: employee.phone,
      gender: employee.gender,
      location: employee.location,
      notes: employee.notes
    });
  }
  get skillsFormArray() {
    return this.profileForm.get('skills');
  }
  populateSkillsForm() {
